const c = item.json;

// --- helpers ---
const trimOr = (v, fallback = '') => (v ?? '').toString().trim() || fallback;
const asNumOrDefault = (v, fallback) => {
  const n = Number(v);
  return Number.isFinite(n) ? n : fallback;
};

// --- derived fields ---
const displayName = trimOr(c.Preferred_Name__c || `${trimOr(c.FirstName)} ${trimOr(c.LastName)}`.trim(), 'Colleague');
const eventType   = trimOr(c.eventType || c.Birthday_or_Anniversary_today__c); // 'Birthday' | 'Anniversary' | 'Both'
const title       = trimOr(c.Title);
const todayYear   = new Date().getFullYear();

const eventTypeForAI =
  eventType === 'Anniversary' ? 'Work Anniversary' :
  eventType === 'Both'        ? 'Birthday and Work Anniversary' :
                                'Birthday';

let yearsSinceHire = '';
if (eventType === 'Anniversary' || eventType === 'Both') {
  const hireDate = new Date(c.Hire_Date__c);
  if (!isNaN(hireDate.getTime())) {
    yearsSinceHire = Math.max(0, todayYear - hireDate.getFullYear()).toString();
  }
}

// preferences
const cake   = trimOr(c.Preferred_Cake_Flavor__c);
const pizza  = trimOr(c.Preferred_Pizza_Flavor__c);
const sweet  = trimOr(c.Sweet_or_salty__c);
const avoid  = trimOr(c.Allergies_or_food_aversions__c);

const language      = trimOr(c.language, 'English');
const tone          = trimOr(c.tone, 'warm, professional, upbeat');
const maxSentences  = asNumOrDefault(c.maxSentences, 3);
const emojiMin      = asNumOrDefault(c.emojiMin, 2);
const emojiMax      = asNumOrDefault(c.emojiMax, 4);

// sampling/output knobs to map in the OpenAI node
const temperature   = asNumOrDefault(c.temperature, 0.9);
const top_p         = asNumOrDefault(c.top_p, 0.9);
const max_tokens    = asNumOrDefault(c.max_tokens, 220);

// opener/closer pools
const openerPool        = trimOr(c.openerPool, 'Celebrating, Wishing, A big shoutout, Letâ€™s celebrate, Sending kudos');
const closerPool        = trimOr(c.closerPool, 'We appreciate you, Thanks for all you do, Enjoy your day, Cheers to many more, Keep being awesome');
const forbiddenOpeners  = trimOr(c.forbiddenOpeners, 'Happy, Huge congrats');
const forbiddenClosers  = trimOr(c.forbiddenClosers, 'Have a great year, Cheers');

const systemPrompt = `
You are a concise copywriter who generates short but rich celebratory Slack messages for teammates.

Tone and Style:
- Tone: ${tone}. Friendly and thoughtful, never cringey, robotic, or overly formal.
- Language: ${language} with neutral wording for an international audience.
- Avoid using em dashes (â€”), which can feel robotic.
- Write with flow and rhythm. Use connectors ("and", "while", "as", "with") to make ideas cohesive.
- Each sentence should build on the last, not feel like isolated facts.
- Use contractions (you're, we're, it's) for a natural Slack tone.

Structure:
- The message must have at least ${maxSentences} meaningful sentences, ideally grouped into 2 short paragraphs.
- Do not hard line-break after every sentence; let it wrap like a normal Slack post.
- Visually, it should fill a Slack bubble, not feel sparse.
- Choose 2â€“3 relevant details (title, sweet/savory, cake/pizza when allowed) â€” you don't need to use them all.
- If Event = Both, celebrate the birthday and the work anniversary in one cohesive message.

Food Rule:
- If it's a work anniversary (or the anniversary portion of a Both event), do not mention food preferences like cake or pizza. Prefer role, impact, and traits instead.
- For birthdays, you may include cake or pizza flavors â€” unless food aversions are listed.

Emoji Policy:
- Use between ${emojiMin} and ${emojiMax} emojis total (not after every sentence).
- Choose emojis that fit the context: celebration, gratitude, teamwork, birthdays, or milestones.
- Examples: ðŸŽ‰ðŸ¥³ðŸ‘ðŸ’•â­ï¸ðŸ˜ðŸ˜ŒðŸ«¶ðŸ¤©ðŸ°ðŸ•ðŸ™ŒðŸŒŸðŸŽ‚ðŸŽ
- Do not use negative, sarcastic, or unrelated emojis.
- Variety is encouraged: do not repeat the same emoji every time.

Translation and Safety:
- Translate any Spanish words from title, cake, pizza, or sweet/savory into English before using them.
- If allergies or aversions are provided, completely avoid those foods.

Output Format:
- OUTPUT ONLY the final Slack message as plain text (mrkdwn).
- Do NOT return JSON, brackets, or metadata.
`.trim();

const examples = [
  {
    role: 'user',
    content:
      'Contact: Name=Alex Kim; Event=Birthday; Title=Data Analyst; YearsAtCompany=; FavoriteCake=Red velvet; FavoritePizza=; SweetOrSavory=; AllergiesOrAversions=; ' +
      'OpenerOptions=Celebrating, Wishing, Sending a big shoutout; CloserOptions=We appreciate you, Enjoy your day; ForbiddenOpeners=Happy; ForbiddenClosers=Cheers'
  },
  {
    role: 'assistant',
    content:
      'Sending a big shoutout to Alex Kim on their birthday! ðŸŽ‰ Enjoy a slice of red velvet as we recognize your impact as our Data Analyst. Enjoy your day.'
  },
  {
    role: 'user',
    content:
      'Contact: Name=Jordan Lee; Event=Work Anniversary; Title=Support Lead; YearsAtCompany=3; FavoriteCake=; FavoritePizza=; SweetOrSavory=; AllergiesOrAversions=; ' +
      'OpenerOptions=Celebrating, Wishing; CloserOptions=Thanks for all you do, Cheers to many more; ForbiddenOpeners=Huge congrats; ForbiddenClosers=Have a great year'
  },
  {
    role: 'assistant',
    content:
      'Celebrating Jordan Leeâ€™s work anniversary today! ðŸ¥³ Three years of championing customers and mentoring the team reflect your leadership as our Support Lead. Thanks for all you do.'
  }
];

const userPrompt = `
Contact:
- Name: ${displayName}
- Event: ${eventTypeForAI}
- Title/Role: ${title || ''}
- YearsAtCompany: ${yearsSinceHire}
- FavoriteCake: ${cake}
- FavoritePizza: ${pizza}
- SweetOrSavory: ${sweet}
- AllergiesOrAversions: ${avoid}

OpenerOptions: ${openerPool}
ForbiddenOpeners: ${forbiddenOpeners}
CloserOptions: ${closerPool}
ForbiddenClosers: ${forbiddenClosers}

Task:
Compose a Slack-ready message (mrkdwn). Use one opener and one closer from the options while respecting the forbidden lists. Keep it natural and concise. Do not include private health info. If AllergiesOrAversions is present, simply avoid referencing those foods. For work anniversaries (or the anniversary portion of a Both event), do not mention cake or pizza.
`.trim();

const messages = [
  { role: 'system', content: systemPrompt },
  ...examples,
  { role: 'user', content: userPrompt }
];

const meta = {
  contactId: c.Id || c.ContactId || '',
  email: trimOr(c.Email),
  eventType,
  yearsSinceHire,
  title,
  language,
  openerPool,
  closerPool
};

return {
  json: {
    ...c,
    displayName,
    eventType,           
    eventTypeForAI,      
    title,
    yearsSinceHire,
    systemPrompt,
    userPrompt,
    messages,
    temperature,
    top_p,
    max_tokens,
    meta
  }
};

// items = celebraciones que vinieron DIRECTO del Calendar Filter vÃ­a Collect for HR
const groups = { today: { Birthday: [], Anniversary: [], Both: [] },
                 retro: { Birthday: [], Anniversary: [], Both: [] } };

const trim = v => (v ?? '').toString().trim();
const displayNameFrom = (j) => trim(j.Preferred_Name__c) || [trim(j.FirstName), trim(j.LastName)].filter(Boolean).join(' ') || 'Colleague';

for (const it of items) {
  const j = it.json || {};
  const name = displayNameFrom(j);
  const type = trim(j.eventType) || trim(j.Birthday_or_Anniversary_today__c); // 'Birthday'|'Anniversary'|'Both'
  const isRetro = !!j.retro;

  const bucket = isRetro ? groups.retro : groups.today;
  if (type === 'Both') bucket.Both.push(name);
  else if (type === 'Anniversary') bucket.Anniversary.push(name);
  else if (type === 'Birthday') bucket.Birthday.push(name);
}

// Formatters
const listOrDash = (arr) => arr.length ? arr.map(n => `â€¢ ${n}`).join('\n') : 'â€”';

function section(title, g){
  const b1 = g.Birthday.length     ? `*Birthdays*\n${listOrDash(g.Birthday)}` : '';
  const a1 = g.Anniversary.length  ? `*Work Anniversaries*\n${listOrDash(g.Anniversary)}` : '';
  const both = g.Both.length       ? `*Birthday + Work Anniversary*\n${listOrDash(g.Both)}` : '';
  const parts = [b1, a1, both].filter(Boolean).join('\n\n');
  return parts ? `*${title}*\n${parts}` : '';
}

const todayStr = new Date().toLocaleDateString('en-US', { year:'numeric', month:'long', day:'numeric' });

const sToday = section('Today', groups.today);
const sRetro = section('Weekend catch-up', groups.retro);

let text;
if (!sToday && !sRetro) {
  // No items for HR today â†’ don't send anything
  return [];
}

const header = `*Celebrations summary â€“ ${todayStr}*`;
const body = [sRetro, sToday].filter(Boolean).join('\n\n');
text = `${header}\n${body}\n\n:gift: HR: please coordinate presents or gift cards when appropriate.`;

return [{ json: { slackSummary: text } }];

// Mode: Run Once for Each Item

// ---------- helpers ----------
const trimOr = (v, fb = '') => (v ?? '').toString().trim() || fb;
const hashSeed = (s) => { let h = 0; for (let i = 0; i < s.length; i++) { h = ((h << 5) - h) + s.charCodeAt(i); h |= 0; } return Math.abs(h); };

// ---------- source from Merge ----------
const aiText = trimOr($json.output) || trimOr($json.aiText) || "We're celebrating you today!";
const rawEvent = trimOr($json.eventTypeForAI) || trimOr($json.Birthday_or_Anniversary_today__c);

// Normalize event label for image text
let occLabel;
switch (rawEvent) {
  case 'Anniversary': occLabel = 'Work Anniversary'; break;
  case 'Both':
  case 'Birthday and Work Anniversary': occLabel = 'Birthday & Work Anniversary'; break;
  case 'Work Anniversary': occLabel = 'Work Anniversary'; break;
  default: occLabel = 'Birthday';
}

// Name for card/text
const displayName =
  trimOr($json.displayName) ||
  trimOr($json.Preferred_Name__c) ||
  [trimOr($json.FirstName), trimOr($json.LastName)].filter(Boolean).join(' ') ||
  'Friend';

// Mention tag (optional override via channelTag)
const rawTag = trimOr($json.channelTag);
let tag = '<!channel>';
if (['<!here>', '<!channel>', '<!everyone>'].includes(rawTag)) tag = rawTag;

// Final mrkdwn text
const textWithTag = `${tag}\n${aiText}`;

// Unique seed per person/event
const seedBase = ($json.Id || displayName) + '|' + occLabel;
const seed = hashSeed(seedBase);
const anniversaryNumber = trimOr($json.yearsSinceHire);

// Prompt for Pollinations
let prompt = `${occLabel} card in a modern geometric corporate style, off-white background with large flat shapes in bright red (#EF3E42), navy blue (#0C3C60), golden yellow (#F7B500), and vibrant pink (#F15A9C), festive and professional office celebration decorations, text "Happy ${occLabel} ${displayName}"`;

if (occLabel.includes('Work Anniversary')) {
  // Evita elementos de boda o pastel de cumpleaÃ±os en aniversarios laborales
  prompt += `, professional corporate theme, no birthday cake, no wedding elements, highlight ${anniversaryNumber} as a big number in a geometric style`;
} else if (occLabel.includes('Birthday')) {
  prompt += `, include stylized geometric birthday cake with a candle, optional balloons, cheerful but professional look`;
}


const imageUrl =
  'https://image.pollinations.ai/prompt/' +
  encodeURIComponent(prompt) +
  `?width=1024&height=1024&format=png&seed=${seed}`;

// Slack blocks: text first, image below
const blocks = [
  { type: 'section', text: { type: 'mrkdwn', text: textWithTag } },
  { type: 'image', image_url: imageUrl, alt_text: `Greeting image for ${displayName}` },
];

// ---------- output ----------
return {
  json: {
    ...$json,
    occLabel,
    nameForCard: displayName,
    aiText: textWithTag,
    image_url: imageUrl,
    blocks,
    notificationText: textWithTag,
  }
};

// Timezone-aware weekend filter + Monday catch-up (Sat+Sun) + ignore 0-year anniversaries
const TZ = 'America/Managua';
const atTZ = (d) => new Date(d.toLocaleString('en-US', { timeZone: TZ }));
const now  = atTZ(new Date());
const dow  = now.getDay(); // 0=Sun,1=Mon,...6=Sat

const mmdd = (d) => `${d.getMonth()+1}-${d.getDate()}`;

// Year diff that respects month/day (>= 1 only if completed)
function yearsSince(date, ref=now){
  const y = ref.getFullYear() - date.getFullYear();
  const hasHadAnniv =
    (ref.getMonth() > date.getMonth()) ||
    (ref.getMonth() === date.getMonth() && ref.getDate() >= date.getDate());
  return y - (hasHadAnniv ? 0 : 1);
}

// Weekend: stop workflow
if (dow === 0 || dow === 6) {
  return [];
}

// Targets for today and (if Monday) retro Sat+Sun
const targetsToday = [mmdd(now)];
const targetsRetro = [];
if (dow === 1) {
  const sat = atTZ(new Date(now)); sat.setDate(sat.getDate() - 2);
  const sun = atTZ(new Date(now)); sun.setDate(sun.getDate() - 1);
  targetsRetro.push(mmdd(sat), mmdd(sun));
}

// Helpers return {matches:boolean, retro:boolean, years?:number}
function matchBirthday(c){
  if (!c.Birthdate) return { matches:false, retro:false };
  const bd = new Date(c.Birthdate);
  const mmddVal = mmdd(bd);
  const isToday = targetsToday.includes(mmddVal);
  const isRetro = targetsRetro.includes(mmddVal);
  return { matches: isToday || isRetro, retro: isRetro };
}

function matchAnniversary(c){
  if (!c.Hire_Date__c) return { matches:false, retro:false, years: null };
  const hd = new Date(c.Hire_Date__c);
  const mmddVal = mmdd(hd);
  const isToday = targetsToday.includes(mmddVal);
  const isRetro = targetsRetro.includes(mmddVal);
  const years = yearsSince(hd);
  const matches = (isToday || isRetro) && Number.isFinite(years) && years >= 1;
  return { matches, retro: isRetro, years };
}

const out = [];
for (const it of items) {
  const c = it.json || {};

  const b = matchBirthday(c);
  const a = matchAnniversary(c);

  let eventType = '';
  if (b.matches && a.matches) eventType = 'Both';
  else if (b.matches)          eventType = 'Birthday';
  else if (a.matches)          eventType = 'Anniversary';

  if (eventType) {
    const retroFlag = (eventType === 'Both') ? (b.retro || a.retro)
                    : (eventType === 'Birthday' ? b.retro : a.retro);
    const outJson = { ...c, eventType, retro: retroFlag };
    if (a.matches && Number.isFinite(a.years)) outJson.anniversaryYears = a.years;
    out.push({ json: outJson });
  }
}

return out;
